# GraphQL Mutation Pattern
# Used for PR thread resolution, creating reviews, etc.

$mutation = @'
mutation ResolveThread($threadId: ID!) {
  resolveReviewThread(input: {threadId: $threadId}) {
    thread {
      id
      isResolved
    }
  }
}
'@

$variables = @{
  threadId = $ThreadId
} | ConvertTo-Json -Compress

try {
  $response = gh api graphql \
    -f query="$mutation" \
    -f variables="$variables" \
    --jq '.data' \
    | ConvertFrom-Json

  if ($response.resolveReviewThread.thread.isResolved) {
    Write-Host "Thread resolved: $ThreadId"
    exit 0
  } else {
    Write-Error "Failed to resolve thread"
    exit 3
  }
} catch {
  Write-Error "GraphQL mutation failed: $_"
  exit 3
}
